[75465805] = {id = 75465805, remark = "――いえ、お待ちください。リーダー様。", type = 1, option_ids = "0", next = 75465807, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465806] = {id = 75465806, remark = "気持ちは嬉しいのですが…\nその前にご相談がございます。", type = 1, option_ids = "0", next = 75465807, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
