[75465807] = {id = 75465807, remark = "リーダー様。\nまず、自分の衛生状態をお考え下さい。", type = 1, option_ids = "0", next = 75465808, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
