, 
[75465809] = {id = 75465809, remark = "ですが、わたくしは先程、\n部屋の掃除を終えて、手を洗っております。", type = 1, option_ids = "0", next = 75465810, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465810] = {id = 75465810, remark = "こほん。つまり…わたくしがリーダー様に\n食べさせれば完璧に清潔、ということでございます。", type = 1, option_ids = "0", next = 75465811, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465811] = {id = 75465811, remark = "というわけで…さあ、どうぞ。リーダー様。\nあーん…でございます。", type = 1, option_ids = "0", next = 75465812, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465812] = {id = 75465812, remark = "…お味のほどはいかがですか？\nその表情ですと、きっと美味しいようですね。", type = 1, option_ids = "0", next = 75465813, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465813] = {id = 75465813, remark = "ああ、ほら。口の端にクリームがついています。\n今拭きますので動かないでくださいね。", type = 1, option_ids = "0", next = 75465814, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465814] = {id = 75465814, remark = "まったく…本当に手のかかる、\nお世話のしがいのあるリーダー様でございます。", type = 1, option_ids = "0", next = -1, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465901] = {id = 75465901, remark = "さて…賊も片付け、プレゼントも\nつつがなく配り終えたようでございます。", type = 1, option_ids = "0", next = 75465902, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", role_admission_type = "75200652:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465902] = {id = 75465902, remark = "リーダー様、お疲れ様でございました。", type = 1, option_ids = "0", next = 75465903, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465903] = {id = 75465903, remark = "それにしても体中に雪をつけて…\n真っ白ではありませんんか。", type = 1, option_ids = "0", next = 75465904, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
