[75465905] = {id = 75465905, remark = "頭にも、肩にも、花にも雪が…それに、リーダー様。\n身体もずいぶんと冷えていらっしゃいます。", type = 1, option_ids = "0", next = 75465906, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465906] = {id = 75465906, remark = "あちこち駆け回って、雪まみれになるのも\n仕方ございませんが…もしや無理なさっていましたか？", type = 1, option_ids = "75506513:75506514", next = 0, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465907] = {id = 75465907, remark = "子供ほど雪まみれになるリーダー様の\n「大丈夫」ほど信頼できない言葉はございません…", type = 1, option_ids = "0", next = 75465909, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465908] = {id = 75465908, remark = "…答えなくても構いません。今のくしゃみで\nリーダー様の体調は把握しましたので。", type = 1, option_ids = "0", next = 75465909, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75465909] = {id = 75465909, remark = "こんなことで風邪をひかれては困りますし、\n汗の不始末は不衛生の最たるものです。", type = 1, option_ids = "0", next = 75465910, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
