[75465910] = {id = 75465910, remark = "それに雪は白くてきれいですが、存外汚れているもの。\n帰ったらすぐに熱いお風呂をご用意しましょう。", type = 1, option_ids = "0", next = 75465911, role_ids = "75200652", role_type = 75200652, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
