[75147442] = {id = 75147442, remark = "――諸君、よくぞ集まってくれた。日々、職務を\n存分に果たしてくれていること、感謝する。", type = 1, option_ids = "0", next = 75147443, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", role_admission_type = "75205010:0:0", role_departure_type = "75201003:0:0,75201005:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_15"}
