[75147503] = {id = 75147503, remark = "簡単に言うと、薬も戦争もぜーんぶ止めて！", type = 1, option_ids = "0", next = 75147504, role_ids = "75205010:75201001", expression = "0:exp_angry", action = "0:angry", role_type = 75201001, position = "195:606:10000,642:606:10050", role_admission_type = "75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "April_Plot_1098"}
