[75147504] = {id = 75147504, remark = "申しわけないが、断らせてもらおう。\nすべてこの国を強大にするために必要なことだ。", type = 1, option_ids = "0", next = 75147505, role_ids = "75205010:75201001", action = "said:0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_23"}
