, 
[75147508] = {id = 75147508, remark = "俺は生まれ育った村をレッドに滅ぼされ、\n両親を失った経験がある。", type = 1, option_ids = "0", next = 75147509, role_ids = "75201002:75205010:75201001", action = "0:said:0", role_type = 75205010, position = "64:606:10000,418:606:10050,772:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_25"}
