[75147510] = {id = 75147510, remark = "王はそのことを深く悼み、人間とレッドの不仲を\n解消しようと尽力していた。だがそれこそ、王の弱さ。", type = 1, option_ids = "0", next = 75147511, role_ids = "75205010:75201001", action = "said:0", role_type = 75205010, position = "195:606:10050,642:606:10000", role_admission_type = "75205010:0:0,75201001:0:0", role_departure_type = "75201004:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_26"}
, 
[75147511] = {id = 75147511, remark = "危険で罪深い存在をそのまま生かそうとするなど\n自らの手を汚したくないための、甘えだ。", type = 1, option_ids = "0", next = 75147512, role_ids = "75205010:75201001", action = "shock:0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_27"}
, 
