, 
[75147516] = {id = 75147516, remark = "レッドは、やはり強い。\n薬を飲んだだけの人間になど到底、敵わぬほどに。", type = 1, option_ids = "0", next = 75147517, role_ids = "75205010", action = "happy", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_30"}
