, 
[75148305] = {id = 75148305, remark = "…改めて、話をしたいと思ってな。\n特に貴君――リーダーと。", type = 1, option_ids = "0", next = 75148306, role_ids = "75205010", action = "0", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_41"}
, 
[75148306] = {id = 75148306, remark = "貴君も人間だ。しかし、武力などと無関係に\nレッドどもの肩を持っているように見える。", type = 1, option_ids = "0", next = 75148307, role_ids = "75205010", action = "shock", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_42"}
, 
[75148307] = {id = 75148307, remark = "なぜ、そんなことをする？\nいったい…何を求めているんだ？", type = 1, option_ids = "75348013:75348014", next = 0, role_ids = "75205010", action = "said", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_43"}
