[75148311] = {id = 75148311, remark = "青臭いことを…\nそんな綺麗ごとで、ここまで来たというのか？", type = 1, option_ids = "0", next = 75148312, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", role_admission_type = "75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_45"}
, 
[75148312] = {id = 75148312, remark = "私も信じられなかったけど、お金がいらない人が\n世の中にはいるってリーダーさんに教えてもらったわ。", type = 1, option_ids = "0", next = 75148313, role_ids = "75201004:75201005", expression = "0:exp_said", action = "0:angry", role_type = 75201005, position = "195:606:10000,642:606:10050", role_admission_type = "75201004:0:0,75201005:0:0", role_departure_type = "75205010:0:0,75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "Shanon_Plot_556"}
, 
[75148313] = {id = 75148313, remark = "…シャノンさんの話はちょっと…\nいえ、なんでもないです…", type = 1, option_ids = "0", next = 75148314, role_ids = "75201002:75201003", expression = "exp_said:0", action = "angry:0", role_type = 75201002, position = "195:606:10050,642:606:10000", role_admission_type = "75201002:0:0,75201003:0:0", role_departure_type = "75201004:0:0,75201005:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "Olivia_Plot_957"}
