, 
[75148315] = {id = 75148315, remark = "リーダーは最高で…\n騎士団長は、最悪ってこと♪", type = 1, option_ids = "0", next = 75148316, role_ids = "75205010:75201001", expression = "0:PoseB", action = "0", role_type = 75201001, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "April_Plot_1111"}
, 
[75148316] = {id = 75148316, remark = "――はは。その目、その雰囲気…\n間違いなく、あの女の娘だな。", type = 1, option_ids = "0", next = 75148317, role_ids = "75205010:75201001", action = "happy:0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_46"}
, 
[75148317] = {id = 75148317, remark = "…あの女って、王の妾のこと？", type = 1, option_ids = "0", next = 75148318, role_ids = "75201003:75201001", expression = "exp_shock:0:0", action = "shock:0:0", role_type = 75201003, position = "195:606:10050,642:606:10000", role_admission_type = "75201003:0:0", role_departure_type = "75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "francesca_Plot_654"}
, 
[75148318] = {id = 75148318, remark = "ああ。\nどうせ「副団長」…アネモネから聞いているだろう。", type = 1, option_ids = "0", next = 75148319, role_ids = "75205010", action = "0:angry:0", role_type = 75205010, position = "418:606:10050", role_departure_type = "75201003:0:0,75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_47"}
, 
[75148319] = {id = 75148319, remark = "あいつも所詮はレッド。優秀ではあったが\n同族の感情に流されがちだったからな。", type = 1, option_ids = "0", next = 75148320, role_ids = "75205010", action = "0:said:0", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_48"}
, 
[75148320] = {id = 75148320, remark = "やっぱり騎士団長さんは、\nレッドのことが嫌いなのねぇ。", type = 1, option_ids = "0", next = 75148321, role_ids = "75201005", expression = "exp_said", action = "said", role_type = 75201005, position = "418:606:10050", role_admission_type = "75201005:0:0", role_departure_type = "75201003:0:0,75205010:0:0,75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "Shanon_Plot_557"}
, 
[75148321] = {id = 75148321, remark = "…ああ、嫌いだとも。幼い頃のこともあるが、\n何よりエイプリルの母が忌々しいやつだったからな。", type = 1, option_ids = "0", next = 75148322, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", role_admission_type = "75205010:0:0", role_departure_type = "75201005:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_49"}
, 
[75148322] = {id = 75148322, remark = "あの女は平民だったが王に結婚を申し込まれ、\n周囲に反対されても…妾になって、城に居座った。", type = 1, option_ids = "0", next = 75148323, role_ids = "75205010", action = "shock", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_50"}
, 
[75148323] = {id = 75148323, remark = "だが、俺が追い出してやった。レッドなどという\n汚らわしい存在は、王族にふさわしくないからな。", type = 1, option_ids = "0", next = 75148324, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_51"}
, 
[75148324] = {id = 75148324, remark = "なっ…じゃあ、エイプリルのお母様が行方をくらましたのは\nあなたの仕業なの！？", type = 1, option_ids = "0", next = 75148325, role_ids = "75201002:75205010", expression = "exp_shock:0", action = "angry:0", role_type = 75201002, position = "195:606:10050,642:606:10000", role_admission_type = "75201002:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "Olivia_Plot_958"}
, 
[75148325] = {id = 75148325, remark = "ああ、城の人間たちも協力してくれた。\n簡単な仕事だった。", type = 1, option_ids = "0", next = 75148326, role_ids = "75201002:75205010", action = "0:angry", role_type = 75205010, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_52"}
, 
[75148326] = {id = 75148326, remark = "王もようやく観念して、隣国の姫だった\nメイの母親と結婚した。", type = 1, option_ids = "0", next = 75148327, role_ids = "75205010", action = "said", role_type = 75205010, position = "418:606:10050", role_departure_type = "75201002:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_53"}
, 
[75148327] = {id = 75148327, remark = "偶然、隣国の国王が亡くなったところだったからな。\nあちらとしても都合が良かっただろう。", type = 1, option_ids = "0", next = 75148328, role_ids = "75205010", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_54"}
, 
[75148328] = {id = 75148328, remark = "…偶然。嘘くさい。", type = 1, option_ids = "0", next = 75148329, role_ids = "75205010:75201003", expression = "0:exp_shock", action = "0:shock", role_type = 75201003, position = "195:606:10000,642:606:10050", role_admission_type = "75201003:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "francesca_Plot_655"}
, 
[75148329] = {id = 75148329, remark = "しかし――まさか、あのうっとうしい女が子供を\n腹に抱えていたとは俺も知らなかった。", type = 1, option_ids = "0", next = 75148330, role_ids = "75205010:75201003", action = "angry:0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_55"}
, 
[75148330] = {id = 75148330, remark = "てっきり、野垂れ死んで終わったかと\n思っていたのだがな。", type = 1, option_ids = "0", next = 75148331, role_ids = "75205010:75201003", action = "0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_56"}
, 
[75148331] = {id = 75148331, remark = "…特別に、その話の続き、教えてあげるね。", type = 1, option_ids = "0", next = 75148332, role_ids = "75205010:75201001", expression = "exp_angry:0", action = "angry:0", role_type = 75201001, position = "195:606:10000,642:606:10050", role_admission_type = "75201001:0:0", role_departure_type = "75201003:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1112"}
, 
[75148332] = {id = 75148332, remark = "エ、エイプリルさん…！？", type = 1, option_ids = "0", next = 75148333, role_ids = "75201004", expression = "exp_shock", action = "shock", role_type = 75201004, position = "418:606:10050", role_admission_type = "75201004:0:0", role_departure_type = "75201001:0:0,75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "Lily_Plot_599"}
, 
[75148333] = {id = 75148333, remark = "お母さんはね、国の隅っこで死にかけてたよ。\nでも旅人が拾ってくれたの。", type = 1, option_ids = "0", next = 75148334, role_ids = "75201001", expression = "exp_angry:0", action = "0", role_type = 75201001, position = "418:606:10050", role_admission_type = "75201001:0:0", role_departure_type = "75201004:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1113"}
, 
[75148334] = {id = 75148334, remark = "それで、わたしの育ての両親がやってた宿屋に\n運ばれたの。で…わたしを産んで、死んじゃった。", type = 1, option_ids = "0", next = 75148335, role_ids = "75201001", expression = "exp_said:0", action = "said:0", role_type = 75201001, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1114"}
, 
[75148335] = {id = 75148335, remark = "王様…お父さんと話して決めたっていう\n『エイプリル』って名前だけ、残して。", type = 1, option_ids = "0", next = 75148336, role_ids = "75201001", expression = "exp_said:0", action = "0", role_type = 75201001, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1115"}
, 
[75148336] = {id = 75148336, remark = "って、宿屋の両親に教えてもらったよ！　だから\n実は、お母さんが死んでるのは知ってたんだよね！", type = 1, option_ids = "0", next = 75148337, role_ids = "75201001", expression = "PoseB:0", action = "0", role_type = 75201001, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1116"}
, 
[75148337] = {id = 75148337, remark = "…ふん。話せば話すほど、気に食わない。", type = 1, option_ids = "0", next = 75148338, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", role_admission_type = "75205010:0:0", role_departure_type = "75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_57"}
, 
[75148338] = {id = 75148338, remark = "どんな悲劇であっても、涙ひとつ見せずに\n話すところが、あの女の血を思い出させる…", type = 1, option_ids = "0", next = 75148339, role_ids = "75205010", action = "0", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_58"}
, 
[75148339] = {id = 75148339, remark = "そう、城を去るときでさえ…そうだった。", type = 1, option_ids = "0", next = 75148340, role_ids = "75205010", action = "shock", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_59"}
, 
[75148340] = {id = 75148340, remark = "『何もかも楽しむべきで…事実、自分の人生は\n楽しかった。だから俺のことは憎まない』と…", type = 1, option_ids = "0", next = 75148341, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_60"}
, 
[75148341] = {id = 75148341, remark = "まぁー、泣きたくても泣かないって。リーダーとかの\n前で泣くならまだしも、騎士団長の前じゃねぇ。", type = 1, option_ids = "0", next = 75148342, role_ids = "75201001", expression = "exp_said:0", action = "angry:0", role_type = 75201001, position = "418:606:10050", role_admission_type = "75201001:0:0", role_departure_type = "75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1117"}
, 
