, 
[75149213] = {id = 75149213, remark = "…目立ちたがり屋の馬鹿。", type = 1, option_ids = "0", next = 75149214, role_ids = "75201002:75201003:75201001", expression = "0:exp_angry:0", action = "0:angry:0", role_type = 75201003, position = "64:606:10000,418:606:10050,772:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "francesca_Plot_645"}
, 
[75149214] = {id = 75149214, remark = "み、皆さんすっごくこっちを見てますよー！", type = 1, option_ids = "0", next = 75149215, role_ids = "75201004:75201005", expression = "exp_shock:0", action = "shock:0", role_type = 75201004, position = "195:606:10050,642:606:10000", role_admission_type = "75201004:0:0,75201005:0:0", role_departure_type = "75201002:0:0,75201003:0:0,75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "Lily_Plot_588"}
, 
[75149215] = {id = 75149215, remark = "ふふ、一斉に飛び掛かられたらおしまいねぇ。", type = 1, option_ids = "0", next = 75149216, role_ids = "75201004:75201005", expression = "0:exp_happy", action = "0:said", role_type = 75201005, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "Shanon_Plot_547"}
, 
[75149216] = {id = 75149216, remark = "…ふっ。今は、部下達を抑えておこう。\n対話で済ませられるものなら、そうすべきだ。", type = 1, option_ids = "0", next = 75149217, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", role_admission_type = "75205010:0:0", role_departure_type = "75201004:0:0,75201005:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_21"}
, 
[75149217] = {id = 75149217, remark = "それで、話はなんだ？", type = 1, option_ids = "0", next = 75149218, role_ids = "75205010", action = "happy", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_22"}
, 
[75149218] = {id = 75149218, remark = "簡単に言うと、薬も戦争もぜーんぶ止めて！", type = 1, option_ids = "0", next = 75149219, role_ids = "75205010:75201001", expression = "0:exp_angry", action = "0:angry", role_type = 75201001, position = "195:606:10000,642:606:10050", role_admission_type = "75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "April_Plot_1098"}
, 
[75149219] = {id = 75149219, remark = "申しわけないが、断らせてもらおう。\nすべてこの国を強大にするために必要なことだ。", type = 1, option_ids = "0", next = 75149220, role_ids = "75205010:75201001", action = "said:0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_23"}
, 
[75149220] = {id = 75149220, remark = "おわー…速攻で断られちゃったよ。\nどうしようね、リーダー。", type = 1, option_ids = "0", next = 75149221, role_ids = "75205010:75201001", expression = "0:PoseB", action = "0", role_type = 75201001, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "April_Plot_1099"}
, 
[75149221] = {id = 75149221, remark = "そ、それでもやり方があるでしょう！\nこんな乱暴な…みんなを傷つけるやり方よりも！", type = 1, option_ids = "0", next = 75149222, role_ids = "75201002:75205010:75201001", expression = "exp_shock:0:0", action = "shock:0:0", role_type = 75201002, position = "64:606:10050,418:606:10000,772:606:10000", role_admission_type = "75201002:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "Olivia_Plot_946"}
, 
[75149222] = {id = 75149222, remark = "ふ…\n気になるならば、きっかけを話してやろう。", type = 1, option_ids = "0", next = 75149223, role_ids = "75201002:75205010:75201001", action = "0:angry:0", role_type = 75205010, position = "64:606:10000,418:606:10050,772:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_24"}
, 
[75149223] = {id = 75149223, remark = "俺は生まれ育った村をレッドに滅ぼされ、\n両親を失った経験がある。", type = 1, option_ids = "0", next = 75149224, role_ids = "75201002:75205010:75201001", action = "0:said:0", role_type = 75205010, position = "64:606:10000,418:606:10050,772:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_25"}
, 
[75149224] = {id = 75149224, remark = "えっ…", type = 1, option_ids = "0", next = 75149225, role_ids = "75201004", expression = "exp_shock", action = "shock", role_type = 75201004, position = "418:606:10050", role_admission_type = "75201004:0:0", role_departure_type = "75201002:0:0,75205010:0:0,75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "Lily_Plot_589"}
, 
[75149225] = {id = 75149225, remark = "王はそのことを深く悼み、人間とレッドの不仲を\n解消しようと尽力していた。だがそれこそ、王の弱さ。", type = 1, option_ids = "0", next = 75149226, role_ids = "75205010:75201001", action = "said:0", role_type = 75205010, position = "195:606:10050,642:606:10000", role_admission_type = "75205010:0:0,75201001:0:0", role_departure_type = "75201004:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_26"}
, 
[75149226] = {id = 75149226, remark = "危険で罪深い存在をそのまま生かそうとするなど\n自らの手を汚したくないための、甘えだ。", type = 1, option_ids = "0", next = 75149227, role_ids = "75205010:75201001", action = "shock:0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_27"}
, 
[75149227] = {id = 75149227, remark = "それで、レッドは潰す！　人間は強くなるぞー！\n…ってなったってこと？", type = 1, option_ids = "0", next = 75149228, role_ids = "75205010:75201001", expression = "0:epx_shock", action = "0", role_type = 75201001, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "April_Plot_1100"}
, 
[75149228] = {id = 75149228, remark = "ああ…以前は、そう考えていた。", type = 1, option_ids = "0", next = 75149229, role_ids = "75205010:75201001", action = "angry:0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_28"}
, 
[75149229] = {id = 75149229, remark = "…どういうこと？", type = 1, option_ids = "0", next = 75149230, role_ids = "75201003", expression = "exp_shock", action = "shock", role_type = 75201003, position = "418:606:10050", role_admission_type = "75201003:0:0", role_departure_type = "75205010:0:0,75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "francesca_Plot_646"}
, 
[75149230] = {id = 75149230, remark = "今回の貴君達のおかげで、\n考えを改めたのだ。", type = 1, option_ids = "0", next = 75149231, role_ids = "75205010", action = "said", role_type = 75205010, position = "418:606:10050", role_admission_type = "75205010:0:0", role_departure_type = "75201003:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_29"}
, 
[75149231] = {id = 75149231, remark = "レッドは、やはり強い。\n薬を飲んだだけの人間になど到底、敵わぬほどに。", type = 1, option_ids = "0", next = 75149232, role_ids = "75205010", action = "happy", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_30"}
, 
[75149232] = {id = 75149232, remark = "その力を捨てるには惜しい。\nならば――活かすまで。", type = 1, option_ids = "0", next = 75149233, role_ids = "75205010", action = "shock", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_31"}
, 
[75149233] = {id = 75149233, remark = "俺が正しい力の使い方を教え、レッド達を\n騎士団のように組ませ…他国を制圧してみせよう。", type = 1, option_ids = "0", next = 75149234, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_32"}
, 
[75149234] = {id = 75149234, remark = "アンレッドと共に我が国の『武力』となれ。\nそれで…すべてを許そう。", type = 1, option_ids = "0", next = 75149235, role_ids = "75205010", action = "happy", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_33"}
, 
[75149235] = {id = 75149235, remark = "…馬鹿馬鹿しいわ。\nあなた、何様のつもり？", type = 1, option_ids = "0", next = 75149236, role_ids = "75201002:75205010", expression = "exp_angry:0", action = "angry:0", role_type = 75201002, position = "195:606:10050,642:606:10000", role_admission_type = "75201002:0:0", role_departure_type = "75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "Olivia_Plot_947"}
, 
[75149236] = {id = 75149236, remark = "ふっ…真面目な提案だったのだが。", type = 1, option_ids = "75349021:75349022", next = 0, role_ids = "75201002:75205010", action = "0:angry", role_type = 75205010, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_34"}
, 
[75149237] = {id = 75149237, remark = "…うん。そんな提案、ありえない。絶対に。", type = 1, option_ids = "0", next = 75149239, role_ids = "75201003:75201004", expression = "exp_angry:0", action = "angry:0", role_type = 75201003, position = "195:606:10050,642:606:10000", role_admission_type = "75201003:0:0,75201004:0:0", role_departure_type = "75201002:0:0,75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "francesca_Plot_647"}
, 
[75149238] = {id = 75149238, remark = "リーダーさんに大賛成です！\nいいよって、言うわけありません！", type = 1, option_ids = "0", next = 75149239, role_ids = "75201003:75201004", expression = "0:exp_angry", action = "0:angry", role_type = 75201004, position = "195:606:10000,642:606:10050", role_admission_type = "75201003:0:0,75201004:0:0", role_departure_type = "75201002:0:0,75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "Lily_Plot_590"}
, 
[75149239] = {id = 75149239, remark = "そうだね。\nわたし達は『武力』になんてならないよっ。", type = 1, option_ids = "0", next = 75149240, role_ids = "75201003:75201004:75201001", expression = "0:0:exp_angry", action = "0:0:angry", role_type = 75201001, position = "64:606:10000,418:606:10000,772:606:10050", role_admission_type = "75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "April_Plot_1101"}
, 
[75149240] = {id = 75149240, remark = "――そうか。ならば、交渉は決裂だ。\n後は部下にすべて任せよう。", type = 1, option_ids = "0", next = 75149241, role_ids = "75205010", action = "said", role_type = 75205010, position = "418:606:10050", role_admission_type = "75205010:0:0", role_departure_type = "75201003:0:0,75201004:0:0,75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_35"}
, 
[75149241] = {id = 75149241, remark = "ち、ちょっと待ってください！\nどこへ行くんですか！", type = 1, option_ids = "0", next = 75149242, role_ids = "75201004:75205010", expression = "exp_shock:0", action = "shock:0", role_type = 75201004, position = "195:606:10050,642:606:10000", role_admission_type = "75201004:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "Lily_Plot_591"}
, 
[75149242] = {id = 75149242, remark = "貴君達が、次に俺のもとへたどりついたときには\n相手をしてやろう。", type = 1, option_ids = "0", next = 75149243, role_ids = "75201004:75205010", action = "0:happy", role_type = 75205010, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_36"}
, 
