, 
[75150115] = {id = 75150115, remark = "リーダーは最高で…\n騎士団長は、最悪ってこと♪", type = 1, option_ids = "0", next = 75150116, role_ids = "75205010:75201001", expression = "0:PoseB", action = "0", role_type = 75201001, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006, dialogue_sound = "April_Plot_1111"}
, 
