, 
[75150117] = {id = 75150117, remark = "…あの女って、王の妾のこと？", type = 1, option_ids = "0", next = 75150118, role_ids = "75201003:75205010:75201001", expression = "exp_shock:0:0", action = "shock:0:0", role_type = 75201003, position = "64:606:10050,418:606:10000,772:606:10000", role_admission_type = "75201003:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "francesca_Plot_654"}
, 
[75150118] = {id = 75150118, remark = "ああ。\nどうせ副リーダー…アネモネから聞いているだろう。", type = 1, option_ids = "0", next = 75150119, role_ids = "75201003:75205010:75201001", action = "0:angry:0", role_type = 75205010, position = "64:606:10000,418:606:10050,772:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_47"}
, 
[75150119] = {id = 75150119, remark = "あいつも所詮はレッド。優秀ではあったが\n同族の感情に流されがちだったからな。", type = 1, option_ids = "0", next = 75150120, role_ids = "75201003:75205010:75201001", action = "0:said:0", role_type = 75205010, position = "64:606:10000,418:606:10050,772:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_48"}
, 
[75150120] = {id = 75150120, remark = "やっぱり騎士団長さんは、\nレッドのことが嫌いなのねぇ。", type = 1, option_ids = "0", next = 75150121, role_ids = "75201005", expression = "exp_said", action = "said", role_type = 75201005, position = "418:606:10050", role_admission_type = "75201005:0:0", role_departure_type = "75201003:0:0,75205010:0:0,75201001:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "Shanon_Plot_557"}
, 
[75150121] = {id = 75150121, remark = "…ああ、嫌いだとも。幼い頃のこともあるが、\n何よりエイプリルの母が忌々しいやつだったからな。", type = 1, option_ids = "0", next = 75150122, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", role_admission_type = "75205010:0:0", role_departure_type = "75201005:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_49"}
, 
[75150122] = {id = 75150122, remark = "あの女は平民だったが王に結婚を申し込まれ、\n周囲に反対されても…妾になって、城に居座った。", type = 1, option_ids = "0", next = 75150123, role_ids = "75205010", action = "shock", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_50"}
, 
[75150123] = {id = 75150123, remark = "だが、俺が追い出してやった。レッドなどという\n汚らわしい存在は、王族にふさわしくないからな。", type = 1, option_ids = "0", next = 75150124, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_51"}
, 
[75150124] = {id = 75150124, remark = "なっ…じゃあ、エイプリルの母が行方をくらましたのは\nあなたの仕業なの！？", type = 1, option_ids = "0", next = 75150125, role_ids = "75201002:75205010", expression = "exp_shock:0", action = "angry:0", role_type = 75201002, position = "195:606:10050,642:606:10000", role_admission_type = "75201002:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "Olivia_Plot_958"}
, 
[75150125] = {id = 75150125, remark = "ああ、城の人間たちも協力してくれた。\n簡単な仕事だった。", type = 1, option_ids = "0", next = 75150126, role_ids = "75201002:75205010", action = "0:angry", role_type = 75205010, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_52"}
, 
[75150126] = {id = 75150126, remark = "王もようやく観念して、隣国の姫だった\nメイの母親と結婚した。", type = 1, option_ids = "0", next = 75150127, role_ids = "75205010", action = "said", role_type = 75205010, position = "418:606:10050", role_departure_type = "75201002:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_53"}
, 
[75150127] = {id = 75150127, remark = "偶然、隣国の国王が亡くなったところだったからな。\nあちらとしても都合が良かっただろう。", type = 1, option_ids = "0", next = 75150128, role_ids = "75205010", action = "happy", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_54"}
, 
[75150128] = {id = 75150128, remark = "…偶然。嘘くさい。", type = 1, option_ids = "0", next = 75150129, role_ids = "75205010:75201003", expression = "0:exp_shock", action = "0:shock", role_type = 75201003, position = "195:606:10000,642:606:10050", role_admission_type = "75201003:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "francesca_Plot_655"}
, 
[75150129] = {id = 75150129, remark = "しかし――まさか、あのうっとうしい女が子供を\n腹に抱えていたとは俺も知らなかった。", type = 1, option_ids = "0", next = 75150130, role_ids = "75205010:75201003", action = "angry:0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_55"}
, 
[75150130] = {id = 75150130, remark = "てっきり、野垂れ死んで終わったかと\n思っていたのだがな。", type = 1, option_ids = "0", next = 75150131, role_ids = "75205010:75201003", action = "0", role_type = 75205010, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_56"}
, 
[75150131] = {id = 75150131, remark = "…特別に、その話の続き、教えてあげるね。", type = 1, option_ids = "0", next = 75150132, role_ids = "75201001:75205010", expression = "exp_angry:0", action = "angry:0", role_type = 75201001, position = "195:606:10050,642:606:10000", role_admission_type = "75201001:0:0", role_departure_type = "75201003:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1112"}
, 
[75150132] = {id = 75150132, remark = "エ、エイプリルさん…！？", type = 1, option_ids = "0", next = 75150133, role_ids = "75201004", expression = "exp_shock", action = "shock", role_type = 75201004, position = "418:606:10050", role_admission_type = "75201004:0:0", role_departure_type = "75201001:0:0,75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "Lily_Plot_599"}
, 
[75150133] = {id = 75150133, remark = "お母さんはね、国の隅っこで死にかけてたよ。\nでも旅人が拾ってくれたの。", type = 1, option_ids = "0", next = 75150134, role_ids = "75201001:75205010", expression = "exp_angry:0", action = "0", role_type = 75201001, position = "195:606:10050,642:606:10000", role_admission_type = "75201001:0:0,75205010:0:0", role_departure_type = "75201004:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1113"}
, 
[75150134] = {id = 75150134, remark = "それで、わたしの育ての両親がやってた宿屋に\n運ばれたの。で…わたしを産んで、死んじゃった。", type = 1, option_ids = "0", next = 75150135, role_ids = "75201001:75205010", expression = "exp_said:0", action = "said:0", role_type = 75201001, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1114"}
, 
[75150135] = {id = 75150135, remark = "王様…お父さんと話して決めたっていう\n『エイプリル』って名前だけ、残して。", type = 1, option_ids = "0", next = 75150136, role_ids = "75201001:75205010", expression = "exp_said:0", action = "0", role_type = 75201001, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1115"}
, 
[75150136] = {id = 75150136, remark = "って、宿屋の両親に教えてもらったよ！　だから\n実は、お母さんが死んでるのは知ってたんだよね！", type = 1, option_ids = "0", next = 75150137, role_ids = "75201001:75205010", expression = "PoseB:0", action = "0", role_type = 75201001, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1116"}
, 
[75150137] = {id = 75150137, remark = "…ふん。話せば話すほど、気に食わない。", type = 1, option_ids = "0", next = 75150138, role_ids = "75201001:75205010", action = "angry", role_type = 75205010, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_57"}
, 
[75150138] = {id = 75150138, remark = "どんな悲劇であっても、涙ひとつ見せずに\n話すところが、あの女の血を思い出させる…", type = 1, option_ids = "0", next = 75150139, role_ids = "75201001:75205010", action = "0", role_type = 75205010, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_58"}
, 
[75150139] = {id = 75150139, remark = "そう、城を去るときでさえ…そうだった。", type = 1, option_ids = "0", next = 75150140, role_ids = "75201001:75205010", action = "shock", role_type = 75205010, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_59"}
, 
[75150140] = {id = 75150140, remark = "『何もかも楽しむべきで…事実、自分の人生は\n楽しかった。だから俺のことは憎まない』と…", type = 1, option_ids = "0", next = 75150141, role_ids = "75201001:75205010", action = "angry", role_type = 75205010, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "MB_Gerald_Plot_60"}
, 
[75150141] = {id = 75150141, remark = "まぁー、泣きたくても泣かないって。リーダーとかの\n前で泣くならまだしも、騎士団長の前じゃねぇ。", type = 1, option_ids = "0", next = 75150142, role_ids = "75201001:75205010", expression = "exp_said:0", action = "angry:0", role_type = 75201001, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1117"}
, 
[75150142] = {id = 75150142, remark = "自分の思うがまま進めたがるし、レッドも嫌いだから\n悲しんだら思うつぼって感じだしさー。", type = 1, option_ids = "0", next = 75150143, role_ids = "75201001:75205010", expression = "exp_said:0", action = "said:0", role_type = 75201001, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1118"}
, 
[75150143] = {id = 75150143, remark = "だからさ、わたしも…きっとお母さんも。\n騎士団長の思い通りに悲しむよりも――", type = 1, option_ids = "0", next = 75150144, role_ids = "75201001:75205010", expression = "exp_angry:0", action = "angry:0", role_type = 75201001, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1119"}
, 
[75150144] = {id = 75150144, remark = "人生を楽しむことのほうが…\n何よりも復讐になるって信じてるよ。", type = 1, option_ids = "0", next = 75150145, role_ids = "75201001:75205010", expression = "PoseB:0", action = "0", role_type = 75201001, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1120"}
, 
[75150145] = {id = 75150145, remark = "エイプリル…", type = 1, option_ids = "0", next = 75150146, role_ids = "75201002", expression = "exp_said", action = "0", role_type = 75201002, position = "418:606:10050", role_admission_type = "75201002:0:0", role_departure_type = "75201001:0:0,75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "Olivia_Plot_959"}
, 
