, 
[75150147] = {id = 75150147, remark = "でしょ！\nえへへ、嫌な気分にできて大成功だ！", type = 1, option_ids = "0", next = 75150148, role_ids = "75201001:75205010", expression = "exp_happy:0", action = "0", role_type = 75201001, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1121"}
, 
[75150148] = {id = 75150148, remark = "…リーダー、みんな、ごめんね。\n長々話しちゃってさ。", type = 1, option_ids = "0", next = 75150149, role_ids = "75201001", expression = "PoseB", action = "0", role_type = 75201001, position = "418:606:10050", role_departure_type = "75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1122"}
, 
[75150149] = {id = 75150149, remark = "さ、戦おっか。\nこんな戦い、さっさと終わらせちゃおう！", type = 1, option_ids = "0", next = -1, role_ids = "75201001", expression = "exp_angry", action = "angry", role_type = 75201001, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007, dialogue_sound = "April_Plot_1123"}
, 
[75150201] = {id = 75150201, remark = "…俺も甘かったな。\nどいつもこいつも、さっさと殺すべきだった。", type = 1, option_ids = "0", next = 75150202, role_ids = "75205010", action = "said", role_type = 75205010, position = "418:606:10050", role_admission_type = "75205010:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_62"}
, 
[75150202] = {id = 75150202, remark = "いや…まだ遅くはないか。貴君達も、その他の\n王族達も…皆殺しにすればいいだけ。", type = 1, option_ids = "0", next = 75150203, role_ids = "75205010", action = "angry", role_type = 75205010, position = "418:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010, dialogue_sound = "MB_Gerald_Plot_63"}
, 
