[75707613] = {id = 75707613, remark = "おじさん…じゃなくて、先代の遺言で、\nクーニャちゃんが跡を継ぐことになったんだ。", type = 1, option_ids = "0", next = 75707614, role_ids = "75200681:75200684", role_type = 75200684, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020008}
, 
[75707614] = {id = 75707614, remark = "でも遺言だからって、こんな若い娘に従えないって\n反対する人達が、派閥を作っちゃって…", type = 1, option_ids = "0", next = 75707615, role_ids = "75200681:75200684", role_type = 75200684, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020008}
, 
[75707615] = {id = 75707615, remark = "だが組織内のもめ事程度で、\nボスが代わる理由とするのは少々弱い。", type = 1, option_ids = "0", next = 75707616, role_ids = "75200681:75200684", role_type = 75200681, position = "195:606:10050,642:606:10000", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020008}
, 
