[75707215] = {id = 75707215, remark = "いや、その試すことを認めたのは私だ。\n私から謝罪させてもらおう。", type = 1, option_ids = "0", next = 75707216, role_ids = "75200684:75200681", role_type = 75200681, position = "195:606:10000,642:606:10050", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75707216] = {id = 75707216, remark = "わわっ！？　ふたりとも、そんなに頭下げないで？\nびっくりしただけだから…！", type = 1, option_ids = "0", next = 75707217, role_ids = "75200391", role_type = 75200391, position = "418:606:10050", role_admission_type = "75200391:50:0", role_departure_type = "75200684:0:0,75200681:0:0", scene_effect_position = "1:2", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
