[75710128] = {id = 75710128, remark = "館の人ってことは～…\nん～…使用人とかが行方不明とかぁ？", type = 1, option_ids = "0", next = 75710129, role_ids = "75200361:75200712", role_type = 75200361, position = "195:606:10050,642:606:10000", role_admission_type = "75200361:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710129] = {id = 75710129, remark = "いえ、使用人ではありません。\nわたくしを慕う方達…とでも言いましょうか。", type = 1, option_ids = "0", next = 75710130, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75200361:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710130] = {id = 75710130, remark = "その方達はいつもわたくしについてきて、\n身の回りの面倒を見てくれているのです。", type = 1, option_ids = "0", next = 75710131, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710131] = {id = 75710131, remark = "それで10日ほど前、その方達が\nわたくしの誕生日が近いことを知ったようでして…", type = 1, option_ids = "0", next = 75710132, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710132] = {id = 75710132, remark = "彼らはわたくしにふさわしい贈り物を、と言って\n屋敷を飛び出していってしまったのです。", type = 1, option_ids = "0", next = 75710133, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710133] = {id = 75710133, remark = "なるほど…\nそれで、今日になっても戻らない、ということですな。", type = 1, option_ids = "0", next = 75710134, role_ids = "75200481:75200712", role_type = 75200481, position = "195:606:10050,642:606:10000", role_admission_type = "75200481:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710134] = {id = 75710134, remark = "ええ。\n数日中には戻ると言っていたのですが…", type = 1, option_ids = "0", next = 75710135, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_departure_type = "75200481:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710135] = {id = 75710135, remark = "後から調べて知ったのですが、\n彼らが向かった山には凶悪なモンスターが出るらしく…", type = 1, option_ids = "0", next = 75710136, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710136] = {id = 75710136, remark = "大変情けないのですが、わたくしひとりでは\n心もとなく、皆さまにお願いをしたいのです。", type = 1, option_ids = "75801011:75801012", next = 0, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710137] = {id = 75710137, remark = "ありがとうございます。\nなんとお礼を申し上げたら良いか…", type = 1, option_ids = "0", next = 75710139, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710138] = {id = 75710138, remark = "ええ。迷惑をかけてしまうような輩ではありますが、\nどうしても心配で…", type = 1, option_ids = "0", next = 75710139, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
