, 
[75710140] = {id = 75710140, remark = "無事に見つかったら落とし前をつけてもらうしか\nありませんね…", type = 1, option_ids = "0", next = 75710141, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710141] = {id = 75710141, remark = "ま～ま～。\nもしかしたらお酒を飲んで帰れないだけかもよぉ～？", type = 1, option_ids = "0", next = 75710142, role_ids = "75200361", role_type = 75200361, position = "418:606:10050", role_admission_type = "75200361:0:0", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710142] = {id = 75710142, remark = "いや、そんなことするの\nあんたぐらいだから。", type = 1, option_ids = "0", next = 75710143, role_ids = "75200361:75200421", role_type = 75200421, position = "195:606:10000,642:606:10050", role_admission_type = "75200421:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710143] = {id = 75710143, remark = "ともかく、何かあったって決まったわけじゃないし、\nひとまず探しに行きましょうか。", type = 1, option_ids = "0", next = 75710144, role_ids = "75200361:75200421", role_type = 75200421, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710144] = {id = 75710144, remark = "メリア、その山までの地図をお願いしてもいい？", type = 1, option_ids = "0", next = 75710145, role_ids = "75200361:75200421", role_type = 75200421, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710145] = {id = 75710145, remark = "いえ、わたくしが一緒にカチコミ…案内いたします。\n元はといえばわたくしの落ち度ですので。", type = 1, option_ids = "0", next = 75710146, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75200361:0:0,75200421:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710146] = {id = 75710146, remark = "ですが、モンスターが出るのですぞ？\nメリア殿がそこまで危険を冒す必要は…", type = 1, option_ids = "0", next = 75710147, role_ids = "75200481", role_type = 75200481, position = "418:606:10050", role_admission_type = "75200481:0:0", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710147] = {id = 75710147, remark = "ただ依頼を任せて自分は待つだけ、というのも\n性に合いませんから。", type = 1, option_ids = "0", next = 75710148, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75200481:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710148] = {id = 75710148, remark = "それに、彼らを一刻も早く見つけどつきたいという\n気持ちが抑えられませんので。", type = 1, option_ids = "0", next = 75710149, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710149] = {id = 75710149, remark = "…やっぱりこのメリアって子、\n見た目に合わない気性してるわね…", type = 1, option_ids = "0", next = 75710150, role_ids = "75200421", role_type = 75200421, position = "418:606:10050", role_admission_type = "75200421:0:0", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710150] = {id = 75710150, remark = "まあ、いいわ。\nそれじゃあ、リーダー！　目的の山に向かいましょ！", type = 1, option_ids = "0", next = -1, role_ids = "75200421", role_type = 75200421, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710201] = {id = 75710201, remark = "山はこの森を抜けた先にある、って言ってたけど…\n結構遠いわね。", type = 1, option_ids = "0", next = 75710202, role_ids = "75200421", role_type = 75200421, position = "418:606:10050", role_admission_type = "75200421:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710202] = {id = 75710202, remark = "…皆さま、申し訳ありません。\nうちのシマのことにお付き合いいただいて…", type = 1, option_ids = "75801021:75801022", next = 0, role_ids = "75200421:75200712", role_type = 75200712, position = "195:606:10000,642:606:10050", role_admission_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
