, 
[75710204] = {id = 75710204, remark = "うむ、リーダーの言う通りですぞ。\nメリア殿は何も気に病まなくて良いのですぞ。", type = 1, option_ids = "0", next = 75710205, role_ids = "75200481", role_type = 75200481, position = "418:606:10050", role_admission_type = "75200481:0:0", role_departure_type = "75200361:0:0,75200421:0:0,75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710205] = {id = 75710205, remark = "宝探しやプライドの依頼を受けてたら、\nこういうところに来るなんてよくあることだしね。", type = 1, option_ids = "0", next = 75710206, role_ids = "75200421", role_type = 75200421, position = "418:606:10050", role_admission_type = "75200421:0:0", role_departure_type = "75200481:0:0,75200712:0:0,75200361:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
