, 
[75710207] = {id = 75710207, remark = "お気遣い、ありがとうございます。\nですが、わたくしは平気ですわ。", type = 1, option_ids = "0", next = 75710208, role_ids = "75200481:75200712", role_type = 75200712, position = "195:606:10000,642:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75200421:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710208] = {id = 75710208, remark = "おお～。メリアってかよわに見えて～\n意外とたくましいんだねぇ？", type = 1, option_ids = "0", next = 75710209, role_ids = "75200361", role_type = 75200361, position = "418:606:10050", role_admission_type = "75200361:0:0", role_departure_type = "75200481:0:0,75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710209] = {id = 75710209, remark = "ふふ、元々は貧しい家でなんと生きてきましたから。\nこれくらい、どうということもありませんわ。", type = 1, option_ids = "0", next = 75710210, role_ids = "75200712:75200361", role_type = 75200712, position = "195:606:10050,642:606:10000", role_admission_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710210] = {id = 75710210, remark = "え…えええっ！？　そ、そうなの！？\n立派な屋敷だったし、てっきりいいとこの育ちかと…", type = 1, option_ids = "0", next = 75710211, role_ids = "75200421:75200712", role_type = 75200421, position = "195:606:10050,642:606:10000", role_admission_type = "75200421:0:0", role_departure_type = "75200361:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710211] = {id = 75710211, remark = "あの屋敷は貰い物です。\nわたくしを慕ってくれている方から贈られたのです。", type = 1, option_ids = "0", next = 75710212, role_ids = "75200421:75200712", role_type = 75200712, position = "195:606:10000,642:606:10050", role_admission_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710212] = {id = 75710212, remark = "屋敷を！？\n何をしたら屋敷なんて贈られるのよ！？", type = 1, option_ids = "0", next = 75710213, role_ids = "75200421:75200712", role_type = 75200421, position = "195:606:10050,642:606:10000", role_admission_type = "75200421:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710213] = {id = 75710213, remark = "特別なことはしていません。ただレッドの力を使って\n人助けをしていただけですから。", type = 1, option_ids = "0", next = 75710214, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75200421:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710214] = {id = 75710214, remark = "わたくしのいた土地では略奪が絶えず、\n常に傷付いた人を見かけました。", type = 1, option_ids = "0", next = 75710215, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710215] = {id = 75710215, remark = "それを見て、困っている人をできうる限り助けたい、\nそう思っていたところに、この力に目覚めたのです。", type = 1, option_ids = "0", next = 75710216, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710216] = {id = 75710216, remark = "この力を使い、時に殴り、時にカチコミに行き…\nやがて、わたくしを慕ってくれる方達が現れたのです。", type = 1, option_ids = "0", next = 75710217, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710217] = {id = 75710217, remark = "彼らはわたくしを『メリア様』『姐さん』と呼び、\n様々な贈り物をしてくれました。", type = 1, option_ids = "0", next = 75710218, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710218] = {id = 75710218, remark = "それで屋敷ももらったわけだねぇ。\nすごいねぇ、リーダーもそう思わない～？", type = 1, option_ids = "0", next = 75710219, role_ids = "75200361", role_type = 75200361, position = "418:606:10050", role_admission_type = "75200361:0:0", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710219] = {id = 75710219, remark = "しかし屋敷ともなると、慕っているというより、\nもはや信者の域ですぞ…", type = 1, option_ids = "0", next = 75710220, role_ids = "75200481:75200361", role_type = 75200481, position = "195:606:10050,642:606:10000", role_admission_type = "75200481:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710220] = {id = 75710220, remark = "ええ。わたくしも屋敷には度肝を抜かれましたが、\n好意を断るのも、と思いまして受け取ったのです。", type = 1, option_ids = "0", next = 75710221, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75200481:0:0,75200361:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710221] = {id = 75710221, remark = "その後も贈り物は受け取るようにしていましたが…\nまさかこんなことになってしまうとは…", type = 1, option_ids = "0", next = 75710222, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
, 
[75710222] = {id = 75710222, remark = "やはり、機を見て一度シメておくべき\nだったのでしょうか…？", type = 1, option_ids = "0", next = 75710223, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
