[75710223] = {id = 75710223, remark = "ええと…まぁ、それは何とも言えないけど…\nまあ、まずは無事にその人達を見つけてからね。", type = 1, option_ids = "0", next = 75710224, role_ids = "75200421", role_type = 75200421, position = "418:606:10050", role_admission_type = "75200421:0:0", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020001}
