[75710745] = {id = 75710745, remark = "酒の匂い？\nそんなものはジブンには感じられませんぞ？", type = 1, option_ids = "0", next = 75710746, role_ids = "75200421:75200481:75200361", role_type = 75200481, position = "64:606:10000,418:606:10050,772:606:10000", role_admission_type = "75200421:0:0,75200481:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007}
, 
[75710746] = {id = 75710746, remark = "私もよ。あんた、酒が飲みたすぎて\n鼻がおかしくなってるんじゃないの？", type = 1, option_ids = "0", next = 75710747, role_ids = "75200421:75200481:75200361", role_type = 75200421, position = "64:606:10050,418:606:10000,772:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007}
, 
[75710747] = {id = 75710747, remark = "えぇ～、そんなことないよぉ～。\nこのあたしが言うんだよぉ～？", type = 1, option_ids = "0", next = 75710748, role_ids = "75200421:75200481:75200361", role_type = 75200361, position = "64:606:10000,418:606:10000,772:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007}
, 
