[75710750] = {id = 75710750, remark = "ニコリータ殿は酒となると止まりませんからな…。\nここはついていくしかないようですぞ。", type = 1, option_ids = "0", next = -1, role_ids = "75200421:75200481", role_type = 75200481, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020007}
, 
[75710801] = {id = 75710801, remark = "ここまでありがとうございます！　あんな上等の毛皮が\nあれば、きっとメリアの姐さんも喜びます！", type = 1, option_ids = "0", next = 75710802, role_ids = "75210072:75210081", role_type = 75206091, position = "195:606:10000,642:606:10050", role_admission_type = "75210072:0:0,75210081:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710802] = {id = 75710802, remark = "何、気にすんな。あんな珍しいモンスターを見たら\n狩人の血が騒ぐってもんよ。", type = 1, option_ids = "0", next = 75710803, role_ids = "75210068:75210098", role_type = 75206092, position = "195:606:10000,642:606:10050", role_admission_type = "75210068:0:0,75210098:0:0", role_departure_type = "75210072:0:0,75210081:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710803] = {id = 75710803, remark = "――なるほど。そういうことだったんですね。", type = 1, option_ids = "0", next = 75710804, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75210068:0:0,75210098:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710804] = {id = 75710804, remark = "えっ！？　あ、姐さん…じゃなくて、メリア様！？\nど、どうしてここに！？", type = 1, option_ids = "0", next = 75710805, role_ids = "75210072:75210081", role_type = 75206091, position = "195:606:10000,642:606:10050", role_admission_type = "75210072:0:0,75210081:0:0", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710805] = {id = 75710805, remark = "あなた達の帰りが遅いので、\nこの方たちに依頼をして迎えに来たのです。", type = 1, option_ids = "0", next = 75710806, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75210072:0:0,75210081:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710806] = {id = 75710806, remark = "あなた達、戻ると言った日をとうに過ぎていることに\n気付いていますか？", type = 1, option_ids = "0", next = 75710807, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710807] = {id = 75710807, remark = "あっ…！　す、すみません！\nでも、もう少しで贈り物が――", type = 1, option_ids = "0", next = 75710808, role_ids = "75210072:75210081", role_type = 75206091, position = "195:606:10000,642:606:10050", role_admission_type = "75210072:0:0,75210081:0:0", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710808] = {id = 75710808, remark = "それもいりません。\nですから、今すぐ狩りをやめなさい。", type = 1, option_ids = "0", next = 75710809, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75210072:0:0,75210081:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710809] = {id = 75710809, remark = "あなた達が狩りをしていることで\n周辺の村に被害が出ています。", type = 1, option_ids = "0", next = 75710810, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710810] = {id = 75710810, remark = "この山のモンスターにも、\n村の人々にもそれぞれ生活があります。", type = 1, option_ids = "0", next = 75710811, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710811] = {id = 75710811, remark = "それを勝手な理由で脅かすことは許されません。\nわかりましたか？", type = 1, option_ids = "0", next = 75710812, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710812] = {id = 75710812, remark = "うっ…わ、わかりました。\n今すぐにやめさせていただきます…", type = 1, option_ids = "0", next = 75710813, role_ids = "75210072:75210081", role_type = 75206091, position = "195:606:10000,642:606:10050", role_admission_type = "75210072:0:0,75210081:0:0", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020006}
, 
[75710813] = {id = 75710813, remark = "はっ…俺達はやめないぜ。\nあの上等な毛皮、見逃すわけにはいかねぇからな！", type = 1, option_ids = "0", next = 75710814, role_ids = "75210068:75210098", role_type = 75206092, position = "195:606:10000,642:606:10050", role_admission_type = "75210068:0:0,75210098:0:0", role_departure_type = "75210072:0:0,75210081:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710814] = {id = 75710814, remark = "なっ、そんな…！？", type = 1, option_ids = "0", next = 75710815, role_ids = "75210072:75210081", role_type = 75206091, position = "195:606:10000,642:606:10050", role_admission_type = "75210072:0:0,75210081:0:0", role_departure_type = "75210068:0:0,75210098:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710815] = {id = 75710815, remark = "リーダー殿、この者たちは…", type = 1, option_ids = "75801081:75801082", next = 0, role_ids = "75200481", role_type = 75200481, position = "418:606:10050", role_admission_type = "75200481:0:0", role_departure_type = "75210072:0:0,75210081:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710816] = {id = 75710816, remark = "そうね。話し合いじゃ止まりそうにないわね…", type = 1, option_ids = "0", next = 75710818, role_ids = "75200421", role_type = 75200421, position = "418:606:10050", role_admission_type = "75200421:0:0", role_departure_type = "75200481:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710817] = {id = 75710817, remark = "うむ。\nこういう者達は一度戦うしかありませんぞ。", type = 1, option_ids = "0", next = 75710818, role_ids = "75200481", role_type = 75200481, position = "418:606:10050", role_admission_type = "75200481:0:0", role_departure_type = "75200421:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710818] = {id = 75710818, remark = "なるほど…\nリーダー様、分かりましたわ。", type = 1, option_ids = "0", next = 75710819, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75200481:0:0,75200421:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710819] = {id = 75710819, remark = "彼らには一度、落とし前をつけさせもらう…\nそのタマを取ってでも、ということですね。", type = 1, option_ids = "0", next = 75710820, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710820] = {id = 75710820, remark = "メ、メリア殿！？\nそこまでは言っておりませんぞ！？", type = 1, option_ids = "0", next = 75710821, role_ids = "75200481", role_type = 75200481, position = "418:606:10050", role_admission_type = "75200481:0:0", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710821] = {id = 75710821, remark = "そうでしたか。失礼いたしましたわ。\nそれでは半殺しに、ということですね。", type = 1, option_ids = "0", next = 75710822, role_ids = "75200481:75200712", role_type = 75200712, position = "195:606:10000,642:606:10050", role_admission_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710822] = {id = 75710822, remark = "わたくしは売られた喧嘩は買うスタイルですので、\n喜んで相手をさせていただきますわ。", type = 1, option_ids = "0", next = 75710823, role_ids = "75200481:75200712", role_type = 75200712, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710823] = {id = 75710823, remark = "遠慮なくぶちかまさせていただきますので、\n吠えヅラかく準備をしていてくださいまし。", type = 1, option_ids = "0", next = -1, role_ids = "75200481:75200712", role_type = 75200712, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710824] = {id = 75710824, remark = "ひぃぃぃっ！\nすみませんでした！　許してください！", type = 1, option_ids = "0", next = 75710825, role_ids = "75210068:75210098", role_type = 75206092, position = "195:606:10000,642:606:10050", role_admission_type = "75210068:0:0,75210098:0:0", role_departure_type = "75200481:0:0,75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
