, 
[75710826] = {id = 75710826, remark = "いいえ、それはなりません。", type = 1, option_ids = "0", next = 75710827, role_ids = "75200712:75200721", role_type = 75200712, position = "195:606:10050,642:606:10000", role_admission_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710827] = {id = 75710827, remark = "っ、どうして！　お前も、殺す、言った！\nこいつら、父さん、母さん、傷付けた！", type = 1, option_ids = "0", next = 75710828, role_ids = "75200712:75200721", role_type = 75200721, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710828] = {id = 75710828, remark = "ええ。それについては\n死をもって償うべき重罪ですわ。", type = 1, option_ids = "0", next = 75710829, role_ids = "75200712:75200721", role_type = 75200712, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710829] = {id = 75710829, remark = "ですが――だからと言って、あなたがこんな\nド腐れ野郎共のために、手を汚すべきではありません。", type = 1, option_ids = "0", next = 75710830, role_ids = "75200712:75200721", role_type = 75200712, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710830] = {id = 75710830, remark = "あなたの手は、ご両親を愛するためにあるのですから。", type = 1, option_ids = "0", next = 75710831, role_ids = "75200712:75200721", role_type = 75200712, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710831] = {id = 75710831, remark = "…父さん、母さんのために…", type = 1, option_ids = "0", next = 75710832, role_ids = "75200721", role_type = 75200721, position = "418:606:10050", role_departure_type = "75200712:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710832] = {id = 75710832, remark = "なんだか分かんねえけど、今の内か…！？", type = 1, option_ids = "0", next = 75710833, role_ids = "75210068:75210098", role_type = 75206092, position = "195:606:10000,642:606:10050", role_admission_type = "75210068:0:0,75210098:0:0", role_departure_type = "75200721:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
