, 
[75710834] = {id = 75710834, remark = "あ…", type = 1, option_ids = "0", next = 75710835, role_ids = "75200721", role_type = 75200721, position = "418:606:10050", role_admission_type = "75200721:0:0", role_departure_type = "75210068:0:0,75210098:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710835] = {id = 75710835, remark = "ふふ、色々あったけど一件落着、かしら。", type = 1, option_ids = "0", next = 75710836, role_ids = "75200421:75200721", role_type = 75200421, position = "195:606:10050,642:606:10000", role_admission_type = "75200421:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
