, 
[75710837] = {id = 75710837, remark = "事件の解決を祝して～、かんぱ～い！", type = 1, option_ids = "0", next = -1, role_ids = "75200721:75200361", role_type = 75200361, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75710901] = {id = 75710901, remark = "村の皆様！\n迷惑をおかけして、本当にすみませんでしたぁ！", type = 1, option_ids = "0", next = 75710902, role_ids = "75210072:75210081", role_type = 75206091, position = "195:606:10000,642:606:10050", role_admission_type = "75210072:0:0,75210081:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
