[75710903] = {id = 75710903, remark = "村の皆さま、わたくしからも謝罪をさせてください。\nこの度は大変申し訳ありませんでした。", type = 1, option_ids = "0", next = 75710904, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", role_admission_type = "75200712:0:0", role_departure_type = "75210072:0:0,75210081:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75710904] = {id = 75710904, remark = "もし謝罪だけで足りなければ\nお好きなだけ焼きをいれても構いません。", type = 1, option_ids = "0", next = 75710905, role_ids = "75200712", role_type = 75200712, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
