[75711709] = {id = 75711709, remark = "だから、この人形――ニヒトの片腕を君にあげたのさ。\n君が戦い続けるために…ね。", type = 1, option_ids = "0", next = 75711710, role_ids = "75200261:75200732", role_type = 75200732, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020008}
