[75711710] = {id = 75711710, remark = "…戦い続けるために、か。", type = 1, option_ids = "0", next = 75711711, role_ids = "75200261:75200732", role_type = 75200261, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020008}
