, 
[75713131] = {id = 75713131, remark = "リーダーのはそもそもないとして…\nオリヴィアのは、流されたんじゃない？", type = 1, option_ids = "0", next = 75713132, role_ids = "75200021:75200151", role_type = 75200151, position = "195:606:10000,642:606:10050", role_admission_type = "75200151:0:0", role_departure_type = "75200561:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010}
, 
