, 
[75713214] = {id = 75713214, remark = "そうだね。じゃ、後は全部任せたから。\nあたしはここで昼寝でも――", type = 1, option_ids = "0", next = 75713215, role_ids = "75200773", role_type = 75200773, position = "418:606:10050", role_admission_type = "75200773:0:0", role_departure_type = "75200792:0:0,75200021:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
