, 
[75713129] = {id = 75713129, remark = "うぅ、仕方ないわね…えーっと大きさからすると\nこれは多分ツバキので、こっちがエヴァンテ…", type = 1, option_ids = "0", next = 75713130, role_ids = "75200561:75200021", role_type = 75200021, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010}
, 
[75713130] = {id = 75713130, remark = "あら？\n水着、ふたりの分しか入ってない…？", type = 1, option_ids = "0", next = 75713131, role_ids = "75200561:75200021", role_type = 75200021, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010}
, 
[75713131] = {id = 75713131, remark = "リーダーのはそもそもないとして…\nオリヴィアのは、流されたんじゃない？", type = 1, option_ids = "0", next = 75713132, role_ids = "75200021:75200151", role_type = 75200151, position = "195:606:10000,642:606:10050", role_admission_type = "75200151:0:0", role_departure_type = "75200561:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010}
, 
[75713132] = {id = 75713132, remark = "そ、そんな！\nどうして私のだけ…くしゅんっ！", type = 1, option_ids = "0", next = 75713133, role_ids = "75200021:75200151", role_type = 75200021, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010}
, 
[75713133] = {id = 75713133, remark = "今日は厄日だわ…はぁ…", type = 1, option_ids = "0", next = -1, role_ids = "75200021:75200151", role_type = 75200021, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010}
, 
[75713201] = {id = 75713201, remark = "これで着替えは終わりっと。\n濡れた服よりはだいぶマシになったかな。", type = 1, option_ids = "0", next = 75713202, role_ids = "75200773", role_type = 75200773, position = "418:606:10050", role_admission_type = "75200773:0:0", role_departure_type = "75200021:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713202] = {id = 75713202, remark = "ごめんなさい、オリヴィアさん、リーダーさん。\n私たちだけ着替えることになって…", type = 1, option_ids = "0", next = 75713203, role_ids = "75200792:75200773", role_type = 75200792, position = "195:606:10050,642:606:10000", role_admission_type = "75200792:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713203] = {id = 75713203, remark = "…水着が入ってなかったんだし、仕方ないわ！\nそれより、これからどうするかを話し合いましょう。", type = 1, option_ids = "0", next = 75713204, role_ids = "75200021:75200792", role_type = 75200021, position = "195:606:10050,642:606:10000", role_admission_type = "75200021:0:0", role_departure_type = "75200773:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713204] = {id = 75713204, remark = "といっても、アイテムは全て流されちゃってるし、\n取れる手段は限られそうだけど…", type = 1, option_ids = "0", next = 75713205, role_ids = "75200021:75200792", role_type = 75200021, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713205] = {id = 75713205, remark = "それじゃあ、他の島まで泳いで、\n助けを求めるのはどうでしょう？", type = 1, option_ids = "0", next = 75713206, role_ids = "75200021:75200792", role_type = 75200792, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713206] = {id = 75713206, remark = "私は泳ぎが得意ですし、\n島を渡るぐらいなら簡単だと思いますが…", type = 1, option_ids = "0", next = 75713207, role_ids = "75200021:75200792", role_type = 75200792, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713207] = {id = 75713207, remark = "それはやめておきましょう。そもそも私達が\n流されたのは、あの大渦のせいだし。", type = 1, option_ids = "0", next = 75713208, role_ids = "75200021:75200792", role_type = 75200021, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713208] = {id = 75713208, remark = "どこに渦があるかわからない以上、\n海に入るのは止めておきましょう。", type = 1, option_ids = "0", next = 75713209, role_ids = "75200021:75200792", role_type = 75200021, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713209] = {id = 75713209, remark = "それじゃあ…\nリーダーさんは、どうしたらいいと思いますか？", type = 1, option_ids = "75801315:75801316", next = 75713210, role_ids = "75200021:75200792", role_type = 75200792, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713210] = {id = 75713210, remark = "…そういえば、この島が無人島って\n決まってなかったね。", type = 1, option_ids = "0", next = 75713211, role_ids = "75200773", role_type = 75200773, position = "418:606:10050", role_admission_type = "75200773:0:0", role_departure_type = "75200021:0:0,75200792:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713211] = {id = 75713211, remark = "そうですね。\n何かいいものが見つかるかもしれません。", type = 1, option_ids = "0", next = 75713212, role_ids = "75200021:75200792", role_type = 75200792, position = "195:606:10000,642:606:10050", role_admission_type = "75200021:0:0,75200792:0:0", role_departure_type = "75200773:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
, 
[75713212] = {id = 75713212, remark = "とにかく島に何日いるかわからない以上、\n食料や水を手に入れなきゃで…", type = 1, option_ids = "0", next = 75713213, role_ids = "75200021", role_type = 75200021, position = "418:606:10050", role_departure_type = "75200792:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020002}
