, 
[75716502] = {id = 75716502, remark = "布被ればなんとかなるかなーって思ったけど…\nうーん、だめだったかー。", type = 1, option_ids = "0", next = 75716503, role_ids = "75200874", role_type = 75200874, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716503] = {id = 75716503, remark = "ゆ…幽霊ですー！？\nと、とうとう幽霊に会っちゃいましたー！？", type = 1, option_ids = "0", next = 75716504, role_ids = "75200041", role_type = 75200041, position = "418:606:10050", role_admission_type = "75200041:0:0", role_departure_type = "75200874:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716504] = {id = 75716504, remark = "いや、落ち着きなよ。\nあの子、明らかに生きてるでしょ。", type = 1, option_ids = "0", next = 75716505, role_ids = "75200041:75200884", role_type = 75200884, position = "195:606:10000,642:606:10050", role_admission_type = "75200884:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716505] = {id = 75716505, remark = "…たしかに生きてるですが、\n生きた人間がここにいるのもおかしい話です。", type = 1, option_ids = "0", next = 75716506, role_ids = "75200211", role_type = 75200211, position = "418:606:10050", role_admission_type = "75200211:0:0", role_departure_type = "75200041:0:0,75200884:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716506] = {id = 75716506, remark = "そもそも、持ち主も誰もここに住んでない\n空き家だったはずですよ！", type = 1, option_ids = "0", next = 75716507, role_ids = "75200211", role_type = 75200211, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716507] = {id = 75716507, remark = "それなのに、どうしてここに\nレッドがいるですか！？", type = 1, option_ids = "0", next = 75716508, role_ids = "75200211", role_type = 75200211, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716508] = {id = 75716508, remark = "あー、それにはちょっと長い理由があるんだよねぇ。\n何から説明したらいいかな…", type = 1, option_ids = "75801621:75801622", next = 0, role_ids = "75200874", role_type = 75200874, position = "418:606:10050", role_admission_type = "75200874:0:0", role_departure_type = "75200211:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716509] = {id = 75716509, remark = "あー、そうだよねー。\n自己紹介はまず名前を言わないとねー。", type = 1, option_ids = "0", next = 75716511, role_ids = "75200874", role_type = 75200874, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716510] = {id = 75716510, remark = "えへへ、ありがとー。\nじゃあまず名前から言うねー。", type = 1, option_ids = "0", next = 75716511, role_ids = "75200874", role_type = 75200874, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716511] = {id = 75716511, remark = "あたしの名前はノースって言うんだー。", type = 1, option_ids = "0", next = 75716512, role_ids = "75200871", role_type = 75200871, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716512] = {id = 75716512, remark = "ちょっと前までは別の国に住んでたんだけど、\n色々あって家がなくなっちゃったんだよねー。", type = 1, option_ids = "0", next = 75716513, role_ids = "75200871", role_type = 75200871, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716513] = {id = 75716513, remark = "でー、ここが空き家になってたからー、\nちょーっと住んでた、ってわけなんだー。", type = 1, option_ids = "0", next = 75716514, role_ids = "75200871", role_type = 75200871, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716514] = {id = 75716514, remark = "なるほど、そうだったんですか。\n事情はわかったです。", type = 1, option_ids = "0", next = 75716515, role_ids = "75200211", role_type = 75200211, position = "418:606:10050", role_admission_type = "75200211:0:0", role_departure_type = "75200874:0:0,75200871:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716515] = {id = 75716515, remark = "…ただ、リッタには幽霊騒ぎとは別に\n不法占拠者がいたという問題が発生しているですが…", type = 1, option_ids = "0", next = 75716516, role_ids = "75200211", role_type = 75200211, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716516] = {id = 75716516, remark = "うう、ともかく幽霊じゃなくてよかったですー！\nリリー、これで安心して豪邸の見学ができます―！", type = 1, option_ids = "0", next = 75716517, role_ids = "75200211:75200041", role_type = 75200041, position = "195:606:10000,642:606:10050", role_admission_type = "75200041:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716517] = {id = 75716517, remark = "まあ、原因がわかってよかったよ。\n今までのことは全部、この子が起こしてたわけね。", type = 1, option_ids = "0", next = 75716518, role_ids = "75200884:75200041", role_type = 75200884, position = "195:606:10050,642:606:10000", role_admission_type = "75200884:0:0", role_departure_type = "75200211:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716518] = {id = 75716518, remark = "今までのこと、全部ー？", type = 1, option_ids = "0", next = 75716519, role_ids = "75200884:75200871", role_type = 75200871, position = "195:606:10000,642:606:10050", role_admission_type = "75200871:0:0", role_departure_type = "75200041:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
