, 
[75716520] = {id = 75716520, remark = "それで屋根裏から足音が聞こえたからここに\n来たわけだが…全てお前がやったんじゃないのか？", type = 1, option_ids = "0", next = 75716521, role_ids = "75200121:75200871", role_type = 75200121, position = "195:606:10050,642:606:10000", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716521] = {id = 75716521, remark = "うーん、足音はあたしだと思うけどー、\n扉とか物とかは知らないかなー。", type = 1, option_ids = "0", next = 75716522, role_ids = "75200121:75200871", role_type = 75200871, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
