[75716523] = {id = 75716523, remark = "すぐ下から叫び声とか物が割れる音がしてー、\n人がいるな―、隠れなきゃなーって足音はたてたけど…", type = 1, option_ids = "0", next = 75716524, role_ids = "75200871", role_type = 75200871, position = "418:606:10050", role_admission_type = "75200871:0:0", role_departure_type = "75200041:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716524] = {id = 75716524, remark = "でも、扉とか下の階の物とかは知らないよー。\nあたし、ここから動いてないからねー。", type = 1, option_ids = "0", next = 75716525, role_ids = "75200871", role_type = 75200871, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716525] = {id = 75716525, remark = "それじゃあ、あたし達のことはここに上がってくる\n直前まで気付いてなかった、ってこと…？", type = 1, option_ids = "0", next = 75716526, role_ids = "75200871:75200884", role_type = 75200884, position = "195:606:10000,642:606:10050", role_admission_type = "75200884:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716526] = {id = 75716526, remark = "…そういうことになるな。\n彼女は心霊現象の原因ではないということか…", type = 1, option_ids = "0", next = 75716527, role_ids = "75200121", role_type = 75200121, position = "418:606:10050", role_admission_type = "75200121:0:0", role_departure_type = "75200871:0:0,75200884:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020009}
, 
[75716527] = {id = 75716527, remark = "そ、それじゃあ、一体誰がこんなこと――\nひぃっ！？", type = 1, option_ids = "0", next = 75716528, role_ids = "75200211:75200121", role_type = 75200211, position = "195:606:10050,642:606:10000", role_admission_type = "75200211:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75716528] = {id = 75716528, remark = "わわわわわー！？\nま、また、物が勝手に浮き始めましたー！？", type = 1, option_ids = "0", next = -1, role_ids = "75200041", role_type = 75200041, position = "418:606:10050", role_admission_type = "75200041:0:0", role_departure_type = "75200211:0:0,75200121:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75716601] = {id = 75716601, remark = "わー！　か、花瓶が勝手に天井まで浮いてます―！？\nな、何が起きてるんですかー！？　リーダーさーん！", type = 1, option_ids = "0", next = 75716602, role_ids = "75200041", role_type = 75200041, position = "418:606:10050", role_admission_type = "75200041:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75716602] = {id = 75716602, remark = "しかも、ミシミシっていう音があちこちから…！\nさっきまで何も聞こえなかったのに…！", type = 1, option_ids = "0", next = 75716603, role_ids = "75200041:75200884", role_type = 75200884, position = "195:606:10000,642:606:10050", role_admission_type = "75200884:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75716603] = {id = 75716603, remark = "家具もさっきより激しく動いているぞ…！\nリーダー、ここはともかく一度出るぞ！", type = 1, option_ids = "0", next = 75716604, role_ids = "75200121", role_type = 75200121, position = "418:606:10050", role_admission_type = "75200121:0:0", role_departure_type = "75200041:0:0,75200884:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75716604] = {id = 75716604, remark = "えー、そんなに慌てなくてもいいと思うよー。", type = 1, option_ids = "0", next = 75716605, role_ids = "75200121:75200871", role_type = 75200871, position = "195:606:10000,642:606:10050", role_admission_type = "75200871:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020005}
, 
[75716605] = {id = 75716605, remark = "な、何を言ってるです！？\nこんなの慌てるに決まってるじゃないですか！", type = 1, option_ids = "0", next = 75716606, role_ids = "75200211:75200871", role_type = 75200211, position = "195:606:10050,642:606:10000", role_admission_type = "75200211:0:0", role_departure_type = "75200121:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020003}
