[75716606] = {id = 75716606, remark = "これぐらいはよくあることだし、\n気にしなくて大丈夫だよー。", type = 1, option_ids = "0", next = 75716607, role_ids = "75200211:75200871", role_type = 75200871, position = "195:606:10000,642:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020003}
