[75716607] = {id = 75716607, remark = "す、すごく肝が据わってます…！\nで、でも、さすがに放ってはおけません！", type = 1, option_ids = "75801623:75801624", next = 0, role_ids = "75200041", role_type = 75200041, position = "418:606:10050", role_admission_type = "75200041:0:0", role_departure_type = "75200211:0:0,75200871:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020003}
, 
[75716608] = {id = 75716608, remark = "心配してくれてるんだねー。\nそれじゃあ、断るのも悪いし一緒に行こうかなー。", type = 1, option_ids = "0", next = 75716610, role_ids = "75200871", role_type = 75200871, position = "418:606:10050", role_admission_type = "75200871:0:0", role_departure_type = "75200041:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020003}
, 
[75716609] = {id = 75716609, remark = "うーん、逃げなくても大丈夫だと思うけど…\nでも、せっかくだし行こうかなー。", type = 1, option_ids = "0", next = 75716610, role_ids = "75200871", role_type = 75200871, position = "418:606:10050", role_departure_type = "75200041:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020003}
, 
[75716610] = {id = 75716610, remark = "誰かと一緒に行動するのって、久しぶりだなー。\nたまにはこういうのもいい気がするよねー。", type = 1, option_ids = "0", next = 75716611, role_ids = "75200871", role_type = 75200871, position = "418:606:10050", role_departure_type = "75200041:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020003}
, 
[75716611] = {id = 75716611, remark = "そんな呑気なこと言ってる場合ですか！\n早く下に下りるですよ！", type = 1, option_ids = "0", next = -1, role_ids = "75200211:75200871", role_type = 75200211, position = "195:606:10050,642:606:10000", role_admission_type = "75200211:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020003}
