[75717805] = {id = 75717805, remark = "不思議だよね。学園の外じゃそう上手くいかないけど…\n学園の中なら、なんでもできた。例外は――", type = 1, option_ids = "0", next = 75717806, role_ids = "75200901", role_type = 75200901, position = "418:606:10050", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010}
