[75717809] = {id = 75717809, remark = "…僕もあれには驚いたよ。\nだから人を傷つけることは、止めさせたんだ。", type = 1, option_ids = "0", next = 75717810, role_ids = "75200901:75200911", role_type = 75200901, position = "195:606:10050,642:606:10000", role_admission_type = "75200901:0:0", expression_position = "1:2", carmera = 51100001, carmera_time = 1, role_shake = "0", bgm = 1020010}
